import React, { useState } from 'react';
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';

const Notes = () => {

  const [note, Setnote] = useState({
    title: '',
    content: ""
  })
const InputEvent=(event)=>{
  // const value = event.target.value;
  // const name = event.target.name;
 
 const {name, value} = event.target;

  setNote((prevData)=>{
    return{
      ...prevData,
      [name]:value,

    }
  })
}

  return (
    <>
      <div className="container">
        <form>
          <input type='text' name="title" value={note.title} onChange={InputEvent} placeholder="Enter Title" autoComplete="off" />
          <textarea rows=" " column="" name="content" value={note.content} onChange={InputEvent} placeholder="Write your notes" />
          <Button className="btn">
            <AddIcon />
          </Button>
        </form>
      </div>

    </>
  );
}

export default Notes;
